import React, { Component } from 'react';
import { TextInput, StyleSheet, Text, View,Button } from 'react-native';

import { Input } from 'react-native-elements';
const styles = StyleSheet.create({
  bigBlue: {
    color: 'blue',
    fontWeight: 'bold',
    fontSize: 30,
  },
  red: {
    color: 'red',
  },
});
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: []

    };
}
componentDidMount() {

    fetch('http://localhost/projects/afghan-wireless-selfcare-portal/project/index.php/api/banner').then((response) => {
        return response.json();
    })
        .then((myJson) => {

            this.setState({
                items: myJson
            });
        })

}

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
    console.log(this.state);
  }

  render() {
    console.log(this.state);
    return (
      <View>
          <Text style={styles.bigblue}>just red</Text>
         <TextInput
          style={styles.bigBlue}
          placeholder="Type here to translate!"
          type= "sumbit"
        />
          <Button style={{width: 50, height: 100, backgroundColor: 'skyblue'}}><Text>asdsadsadsadsadsadsadsadsad in</Text></Button>
      </View>
    );
  }
}
